mapper.select:
SELECT * FROM OrderInfo


        AND status=${status}

        AND openid=${openid}

        AND commopenid=${commopenid}

        AND timeend > #{timestart}

          AND timeend < #{timeend} 

ORDER  by id desc

SELECT count(id) as  id,sum(amount) as amount,sum(bonus) as bonus,sum(comm) as comm FROM OrderInfo


        AND status=${status}

        AND openid=${openid}

        AND commopenid=${commopenid}

        AND timeend > #{timestart}

          AND timeend < #{timeend} 
  ORDER  by openid desc
